mapper.select:
/* BoardMapper.selectAllBoard */ SELECT SQL_CALC_FOUND_ROWS BOARD.* FROM (SELECT BOARD_ID AS board_id , B_SUBJECT AS b_subject , B_CONTENT AS b_content , USER_IDX AS user_idx , USER_NM AS user_nm , DATE_FORMAT(B_DATE, CONCAT('%Y-%m-%d', ' ', '%H:%i')) AS b_date , DATE_FORMAT(B_UPD_DATE, CONCAT('%Y-%m-%d', ' ', '%H:%i')) AS b_upd_date FROM TB_BOARD ORDER BY B_DATE DESC) AS BOARD WHERE 1=1
AND USER_NM LIKE CONCAT('%', #{srch_text} ,'%')
AND B_SUBJECT LIKE CONCAT('%', #{srch_text} ,'%')
AND B_CONTENT LIKE CONCAT('%', #{srch_text}, '%')
LIMIT ${start}, ${end}

/* BoardMapper.selectMyBoard */ SELECT SQL_CALC_FOUND_ROWS BOARD.* FROM (SELECT BOARD_ID AS board_id , B_SUBJECT AS b_subject , B_CONTENT AS b_content , DATE_FORMAT(B_DATE, CONCAT('%Y-%m-%d', ' ', '%H:%i')) AS b_date , DATE_FORMAT(B_UPD_DATE, CONCAT('%Y-%m-%d', ' ', '%H:%i')) AS b_upd_date , USER_IDX AS user_idx , USER_NM AS user_nm FROM TB_BOARD WHERE USER_IDX = #{user_idx} ORDER BY B_DATE DESC) AS BOARD WHERE 1=1
AND USER_NM LIKE CONCAT('%', #{srch_text} ,'%')
AND B_SUBJECT LIKE CONCAT('%', #{srch_text} ,'%')
AND B_CONTENT LIKE CONCAT('%', #{srch_text}, '%')
LIMIT ${start}, ${end}

mapper.insert:
/* BoardMapper.createItem */
INSERT INTO
TB_BOARD(B_SUBJECT, B_CONTENT, USER_IDX, USER_NM, B_DATE)
VALUES(
		  #{b_subject}
		, #{b_content}
		, #{user_idx}
		, #{user_nm}
		, NOW()
	  )

mapper.update:
/* BoardMapper.updateItem */
UPDATE TB_BOARD
SET

	  B_SUBJECT = #{b_subject}		


	, B_CONTENT = #{b_content}

	, B_UPD_DATE = NOW()
WHERE
BOARD_ID = #{board_id}

mapper.delete:
/* BoardMapper.deleteItem */
DELETE FROM TB_BOARD
WHERE BOARD_ID = #{board_id}
