mapper.select:
		/* BoardMapper.selectAllBoard */
		SELECT SQL_CALC_FOUND_ROWS
			   board.*
	    FROM  
	    	   (SELECT * FROM BOARD ORDER BY bDate DESC) AS board
   	    LIMIT ${start}, ${end}

/* BoardMapper.selectMyBoard */
SELECT SQL_CALC_FOUND_ROWS 
	   board.*
FROM 
	   (SELECT * FROM BOARD WHERE uIdx=#{uIdx} ORDER BY bDate DESC) AS board
LIMIT ${start}, ${end}

mapper.insert:
/* BoardMapper.createItem */
INSERT INTO
BOARD(bSubject, bContent, uIdx, uNm, bDate)
VALUES(
		  #{bSubject}
		, #{bContent}
		, #{uIdx}
		, #{uNm}
		, now()
	  )

mapper.update:
/* BoardMapper.updateItem */
UPDATE BOARD
SET

	  bSubject = #{bSubject}		


	, bContent=#{bContent}

	, bDate=now()
WHERE
bIdx = #{bIdx}

mapper.delete:
/* BoardMapper.deleteItem */
DELETE FROM BOARD
WHERE bIdx = #{bIdx}
