mapper.select:
SELECT
	COUNT(1)
FROM TB_LIKE
WHERE 1=1 
	AND USER_IDX = #{user_idx}
	AND BOARD_ID = #{board_id}

mapper.insert:
INSERT INTO
	TB_LIKE(LIKE_ID, USER_IDX, BOARD_ID)
VALUES(
          #{user_idx}  
        , #{user_idx}  
        , (SELECT IFNULL(MAX(BOARD_ID), 1) FROM TB_BOARD)  
	   )

mapper.delete:
/* LikedMapper.deleteItemLike */
DELETE FROM TB_LIKE
WHERE 1=1
	AND USER_IDX = #{user_idx}
	AND BOARD_ID = #{board_id}
