mapper.update:
/* TodoMapper.updateTodo */
	UPDATE TB_TODO
	SET
	  TD_NM = #{td_nm}
	, TD_UPD_DATE = NOW()

		, TD_LAST_DATE = #{td_last_date}



			, TD_TP = '00'


			, TD_TP = '01'


			, TD_TP = '02'


			, TD_TP = '99'


	WHERE 1=1
	AND TD_ID = #{td_id}

mapper.delete:
/* TodoMapper.deleteTodo */
	DELETE FROM TB_TODO 
	WHERE TD_ID = #{td_id}

mapper.insert:
/* TodoMapper.createTodo */
	INSERT INTO
	TB_TODO(TD_NM, TD_TP, TD_DATE ,USER_IDX, USER_NM, TD_LAST_DATE)
	VALUES(
				#{td_nm}
			  , #{td_tp}
			  , NOW()
			  , #{user_idx}
			  , #{user_nm}
			  , #{td_last_date}
		  )
